from openpyxl.utils.cell import coordinate_from_string, column_index_from_string

def appendAfter(ws, dfToAppend):
  for _temp_index, _temp_row in dfToAppend.iterrows():
    ws.append(_temp_row)

def appendIn(ws, dfToAppend, fromCell, frmtCols):
  _temp_xy = coordinate_from_string(fromCell)
  _temp_col_index = column_index_from_string(_temp_xy[0])
  _temp_row_index = _temp_xy[1]
  for _temp_index, _temp_row in dfToAppend.iterrows():
    _temp_col_i = _temp_col_index
    for _temp_key, _temp_value in _temp_row.items():
      if _temp_key in frmtCols:
        ws.cell(row=_temp_row_index, column=_temp_col_i).value = frmtCols[_temp_key]%(_temp_value)
      else:
        ws.cell(row=_temp_row_index, column=_temp_col_i).value = _temp_value
      _temp_col_i = _temp_col_i + 1
    _temp_row_index = _temp_row_index + 1      
